import "./ChartBar.css"
const Chart = props => {
    let barFillHeight = "0%";

    if (props.maxValue > 0){
        barFillHeight = Math.round((props.value/ props.maxValue) * 100) + "%";
    }
    return (
    <div className="chart-bar">
        <div className="class-bar__inner">
            <div className="class-bar__fill" style={{height: barFillHeight}}></div>
        </div>
        <div className="class-bar__label"></div>
    </div>
    );

}

export default Chart;
